vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for checkerboard pattern
    vec2 st = uv * 10.0;

    // Create rotating checkerboard patterns
    float angle = iTime * 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedSt = rot * (st - 5.0) + 5.0;

    // Generate two different checkerboard patterns with noise
    float pattern1 = step(0.5, fract(rotatedSt.x * 0.5) + fract(rotatedSt.y * 0.5));
    float pattern2 = step(0.5, fract((rotatedSt.x + iTime) * 0.3) + fract((rotatedSt.y + iTime) * 0.7));

    // Mix patterns with noise and turbulence
    float mixAmount = turbulent(vec2(st)) * 0.5 + 0.5;
    vec3 color1 = hsl(0.6 + sin(iTime)*0.1, 0.8, 0.6);
    vec3 color2 = hsl(0.2 + cos(iTime*0.7)*0.1, 0.8, 0.5);

    // Combine colors and patterns
    vec3 finalColor = mix(color1, color2, mixAmount);
    finalColor = mix(finalColor, vec3(1.0), pattern1 * 0.4 + pattern2 * 0.6);

    // Add transparency based on time
    float alpha = smoothstep(0.0, 1.0, sin(iTime*0.5) * 0.5 + 0.5);

    return vec4(finalColor, alpha);
}
