vec4 Effect2(vec2 uv) {
    // Create a pulsating wave pattern using time and position
    float wave = sin(iTime + uv.x * 2.0 + uv.y * 3.0);
    wave = (wave + 1.0) * 0.5; // Scale to 0-1 range

    // Add pulsation effect with time modulation
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(
        (iTime * 0.1 + uv.x) * PI, // Hue based on position and time
        1.0,                       // Saturation
        wave * pulse               // Value modulated by pulsation
    ));

    return vec4(color * 0.5 + 0.5, 1.0); // Add some brightness to the final color
}
