vec4 Effect2(vec2 uv) {
    // Create rotating pattern by offsetting UV coordinates with time-based sine waves
    vec2 st = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 3.0;

    // Add noise and turbulence to create dynamic patterns
    float noiseValue = turbulent(st);

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        fract(noiseValue + iTime),
        0.7,
        0.5
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
