vec4 Effect2(vec2 uv) {
    // Create a moving pattern by combining UV and time with sine functions
    vec2 st = uv + sin(iTime * 0.5 + uv.x * pi) * 0.5;

    // Generate two different color variations using HSL
    vec3 c1 = hsl(
        sin(st.x * 5.0 + iTime) * 0.5 + 0.5,
        abs(noise(vec2(uv.x, uv.y))) * 0.7 + 0.3,
        0.6
    );

    vec3 c2 = hsl(
        sin(st.y * 5.0 + iTime * 3.0) * 0.5 + 0.5,
        abs(noise(vec2(uv.y, uv.x))) * 0.7 + 0.3,
        0.6
    );

    // Mix the colors with a time-based blend
    float blend = sin(iTime * 0.1 + uv.x * pi) * 0.5 + 0.5;
    vec3 finalColor = mix(c1, c2, blend);

    return vec4(hsl2rgb(finalColor), 1.0);
}
