vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create spiral pattern using sine waves
    float frequency = 5.0;
    float spiral = sin(r * frequency + theta * 3.0) * 0.5 + 0.5;

    // Add time-based color changes
    vec3 colorHSL = vec3(
        sin(iTime * 0.2 + theta) * 0.5 + 0.5,  // Hue
        0.7,                                    // Saturation
        spiral * 0.6 + 0.4                      // Lightness
    );

    // Convert HSL to RGB and add turbulence effect
    vec3 rgbColor = hsl2rgb(colorHSL);
    float turb = turbulent(vec2(r * frequency, iTime * 0.1));
    rgbColor *= mix(0.8, 1.2, turb);

    // Add noise for extra detail
    vec2 noiseUV = uv * 5.0 + iTime;
    float noiseVal = noise(noiseUV) * 0.3;
    rgbColor += noiseVal;

    return vec4(rgbColor, 1.0);
}
