vec4 Effect2(vec2 uv) {
    // Center the UV coordinates and convert to polar coordinates
    vec2 centeredUV = uv * 2.0 - 1.0;
    float radius = length(centeredUV);

    // Create a rotating pattern using time
    float angle = atan2(centeredUV.y, centeredUV.x) + iTime * 0.5 + radius * 0.5;

    // Generate RGB values from the angle
    vec3 color;
    color.r = sin(angle + 0.0);
    color.g = sin(angle + 2.094); // 120 degrees in radians (2pi/3)
    color.b = sin(angle + 4.188); // 240 degrees in radians (4pi/3)

    // Scale colors by radius to create a circular pattern
    color *= radius * 0.5 + 0.5;

    // Add some turbulence for extra visual interest
    float turb = turbulent(uv * 10.0 + iTime);
    color += turb * 0.3;

    return vec4(color, 1.0);
}
