vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation and expansion
    angle += iTime * 0.5;
    radius *= sin(iTime) * 0.5 + 0.75;

    // Create expanding rings pattern
    float ring = mod(radius * 10.0, 2.0);
    vec3 color = hsl(angle / (2.0 * PI), 0.6, 0.5);

    // Add turbulence for dynamic movement
    vec2 noiseUV = uv + vec2(iTime) * 0.5;
    float turb = turbulent(noiseUV * 10.0);

    // Combine patterns and colors
    color *= hsl2rgb(vec3(ring, 0.8, 0.7));
    color += turb * 0.3;

    return vec4(color, 1.0);
}
