vec4 Effect2(vec2 uv) {
    // Combine time with pixel position for dynamic patterns
    vec3 p = vec3(uv * 2.0 - 1.0, sin(iTime * 0.5));

    // Generate noise values at different scales
    float n1 = noise(p);
    float n2 = noise(p * 10.0 + iTime);
    float t = turbulent(p * 3.0 + iTime);

    // Create pulsating color shifts using HSL
    vec3 hslColor = vec3(
        (n1 + sin(iTime) * 0.5 + 0.5) * 240.0,  // Hue variation over time and space
        abs(n2) * 0.7 + 0.3,                       // Saturation modulation
        abs(t) * 0.6 + 0.4                         // Brightness pulsation
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
