vec4 Effect2(vec2 uv) {
    // Create undulating sine wave patterns
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0) * 0.5;
    float wave2 = cos(uv.y * 6.0 - iTime * 3.0) * 0.4;

    // Combine waves and add turbulence
    float height = wave1 + wave2 + turbulent(vec3(uv, iTime)) * 0.2;

    // Create smooth color transitions using HSL
    vec3 colorHSL = vec3(
        mod(iTime * 0.1 + uv.x * 0.5 + uv.y * 0.7, 1.0),  // Hue
        0.6 + abs(sin(iTime * 0.2)) * 0.4,                  // Saturation
        0.5 + height * 0.3                                 // Lightness
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(colorHSL), 1.0);
}
