vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 uv_centered = uv * 2.0 - 1.0;
    float r = length(uv_centered);
    float theta = atan2(uv_centered.y, uv_centered.x);
    // Add rotation based on time
    theta += iTime * 1.5;
    // Create smooth color transitions using HSL
    vec3 hsl_color = vec3(
        theta / (pi * 2.0),
        0.7,
        pow(r, 2.0) * 0.6 + 0.4
    );
    // Convert HSL to RGB and add noise for texture
    vec3 color = hsl2rgb(hsl_color);
    color += noise(uv * 100.0 + iTime) * 0.05;
    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
