vec4 Effect2(vec2 uv) {
    // Create rotating RGB sine waves with time-based animation
    float t = iTime * 0.5;

    // Calculate base sine wave patterns for each color channel
    vec3 rgb = vec3(
        sin(uv.x * 10.0 + t) * 0.5 + 0.5,
        cos(uv.y * 10.0 + t + 120.0) * 0.5 + 0.5,
        sin(uv.x * uv.y * 10.0 + t + 240.0) * 0.5 + 0.5
    );

    // Add noise and turbulence for dynamic effect
    vec2 st = uv * 10.0;
    float n = noise(st + vec2(t)) * 0.3;
    rgb += n;

    // Create rotating pattern by mixing colors with time offset
    rgb = vec3(
        rgb.r * sin(t) + rgb.g * cos(t),
        rgb.g * sin(t) + rgb.b * cos(t + 120.0),
        rgb.b * sin(t + 240.0) + rgb.r * cos(t)
    );

    return vec4(rgb, 1.0);
}
