vec4 Effect2(vec2 uv) {
    // Create time-varying coordinates
    vec2 st = uv * 5.0 + iTime;

    // Generate multiple sine wave layers with varying frequencies and phases
    float pattern = 0.0;
    for (int i = 1; i <= 4; i++) {
        float freq = pow(2.0, float(i));
        float amp = 0.5 / freq;
        pattern += sin(st.x * freq + iTime) * amp;
        pattern += sin(st.y * freq + iTime * 0.5 + float(i)) * amp;
    }

    // Add noise and turbulence
    vec2 noiseSt = uv * 10.0 + iTime * 0.5;
    float noiseVal = noise(noiseSt);
    pattern += turbulent(noiseSt) * 0.3;

    // Create RGB cycling effect
    float hue = fract(iTime * 0.1 + st.x * 0.2 + st.y * 0.3) * PI * 2.0;
    vec3 color = hsl(hue, 0.8, 0.6);

    // Combine pattern with color
    float finalPattern = (pattern + noiseVal) * 0.5 + 0.5;
    color *= finalPattern;

    return vec4(color, 1.0);
}
