vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create multiple layers of stars for depth effect
    for(float i = 1.0; i < 6.0; i++) {
        float scale = pow(2.0, i * 2.0); // Exponential scaling for star field layers

        // Add noise and turbulence to create organic movement
        vec3 noiseOffset = vec3(turbulent(uv * scale + iTime), turbulent(uv * scale + iTime + 1.0), 0.0);

        // Calculate distance from center with pulsing effect
        float dist = length(uv * scale + noiseOffset.xy * 0.5) * 0.7;
        dist += sin(dist * 2.0 + iTime) * 0.3; // Add pulsing animation

        // Create star glow effect using HSL color space
        vec3 starColor = hsl(
            fract(iTime * 0.1 + dist * 0.5), // Hue variation over time and distance
            1.0, // Full saturation for vibrant stars
            max(0.0, sin(dist * 2.0 - iTime) * 0.5 + 0.5) // Pulsing brightness
        );

        // Combine star layers with additive blending
        color += hsl2rgb(starColor) * (1.0 / scale);
    }

    return vec4(color, 1.0);
}
