vec4 Effect2(vec2 uv) {
    // Animate the pattern over time
    float time = iTime * 0.5;

    // Create a rotating pattern by transforming UV coordinates
    vec2 st = uv * 10.0 + time;
    st = (st - 0.5) * mat2(cos(time), sin(time), -sin(time), cos(time)) + 0.5;

    // Add noise and turbulence to create a dynamic pattern
    float n1 = noise(st);
    float n2 = noise(st * 2.0 + time);
    float n3 = noise(st * 4.0 + time * 2.0);

    // Combine noise layers with different frequencies
    float value = (n1 + n2 * 0.5 + n3 * 0.25) * 0.5 + 0.5;

    // Create a color palette using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        fract((st.x + st.y) * 4.0 + time),  // Hue
        1.0,                                 // Saturation
        value                                // Lightness
    ));

    return vec4(color, 1.0);
}
