vec4 Effect2(vec2 uv) {
    // Create a grid of points using UV coordinates
    vec2 st = uv * 10.0;
    vec2 ipos = floor(st);

    // Animate the grid over time
    float time = iTime * 0.5;
    vec2 pos = sin(vec2(time + ipos.x, time*0.7 + ipos.y)) * 0.5 + 0.5;

    // Calculate distance from center
    vec2 center = vec2(0.5);
    float dist = length(pos - center);

    // Create color transitions using HSL
    vec3 color = hsv2rgb(vec3(
        (dist * 0.7 + time) * 180.0,
        0.6 + turbulent(vec3(st, time)) * 0.4,
        0.5 + sin(time + dist * 10.0) * 0.5
    ));

    // Add noise and turbulence for more detail
    float noiseVal = noise(vec2(pos.x*10.0, pos.y*10.0)) * 0.3;
    color *= 1.0 + noiseVal;

    // Create particle effect with alpha based on distance
    float alpha = smoothstep(0.8, 1.0, 1.0 - dist);
    alpha *= 0.5 + turbulent(st) * 0.5;

    return vec4(color, alpha);
}
