vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Create cell coordinates
    vec2 cell = floor(st);
    vec2 pos = fract(st);

    // Add time-based movement
    float time = iTime * 0.5;

    // Calculate noise values for each cell
    float n1 = turbulent(vec3(cell + time, 0.0));
    float n2 = turbulent(vec3(cell - time, 0.0));

    // Create color transitions using HSL
    vec3 color1 = hsl(n1 * 0.5 + 0.5, 0.8, 0.6);
    vec3 color2 = hsl(n2 * 0.5 + 0.5, 0.8, 0.4);

    // Interpolate between colors based on position
    vec3 finalColor = mix(color1, color2, pos.x);

    // Add dynamic movement using sin function
    float movement = sin(time + cell.x * 0.5) * 0.5 + 0.5;
    finalColor *= movement;

    // Convert HSL to RGB for output
    vec3 rgbColor = hsl2rgb(finalColor);

    return vec4(rgbColor, 1.0);
}
