vec4 Effect2(vec2 uv) {
    // Animate coordinates over time
    vec2 st = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 3.0;

    // Create flowing sine wave pattern with multiple frequencies
    float wave1 = sin(st.x * 2.0 + iTime) * 0.5;
    float wave2 = sin(st.y * 3.0 + iTime * 0.5);
    float pattern = (wave1 + wave2) * 0.5 + 0.5;

    // Add noise for organic feel
    vec2 noiseUV = uv * 4.0 + vec2(iTime * 0.5, 0.0);
    float noiseVal = noise(noiseUV) * 0.3;
    pattern += noiseVal;

    // Create dynamic color transitions using HSV
    vec3 hsvColor = vec3(
        fract(pattern + iTime * 0.1),   // Hue variation over time
        0.8,                            // Saturation
        pattern                         // Value tied to pattern intensity
    );
    vec3 rgbColor = hsv2rgb(hsvColor);

    // Add turbulence for more complex movement
    vec3 turbPos = vec3(uv * 10.0 + iTime, 0.0);
    float turb = turbulent(turbPos) * 0.5;
    pattern += turb;

    // Final color with alpha
    return vec4(rgbColor, 1.0);
}
