vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Create pulsating effect using sine wave
    float pulse = sin(iTime * 0.5 + length(st)) * 0.5 + 0.5;

    // Calculate cell position in grid
    vec2 cell = floor(st);

    // Generate vibrant colors using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3((cell.x + cell.y) * 0.1 + iTime * 0.2, 0.8, 0.6));
    vec3 color2 = hsl2rgb(vec3((cell.x - cell.y) * 0.1 + iTime * 0.3, 0.7, 0.5));

    // Mix colors based on pulsation
    vec3 finalColor = mix(color1, color2, pulse);

    // Add noise for extra texture
    float noiseValue = noise(st * 0.5 + iTime) * 0.2;
    finalColor += noiseValue;

    // Create grid lines with pulsating width
    float line = sin(iTime + length(cell)) * 0.3 + 0.7;
    if (fract(st.x) < line || fract(st.y) < line) {
        finalColor *= 1.5; // Brighten grid lines
    }

    return vec4(finalColor, 1.0);
}
