vec4 Effect2(vec2 uv) {
    // Scale coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Get cell position in grid
    vec2 pos = fract(st);

    // Add noise and time variation
    float noiseVal = turbulent(pos + iTime*0.5);

    // Create pulsating color pattern using HSL
    vec3 hslColor = vec3(
        (pos.x + pos.y) * 0.5,   // Hue varies across grid
        1.0,                     // Full saturation
        0.5 + sin(iTime + pos.x*2.0 + pos.y*3.0)*0.4  // Pulsating lightness
    );

    // Convert to RGB and add noise effect
    vec3 rgb = hsl2rgb(hslColor) * (1.0 + noiseVal*0.5);

    // Combine colors with some blending
    vec3 finalColor = rgb * sin(iTime*0.5 + pos.x*4.0 + pos.y*6.0)*0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
