vec4 Effect2(vec2 uv) {
    // Create pulsing circles using sine waves
    float time = iTime * 0.5;

    // Calculate distance from center
    vec2 pos = uv - 0.5;
    float r = length(pos);

    // Create multiple layers of pulsing circles
    float c1 = sin(r * 2.0 + time) * 0.5;
    float c2 = sin(r * 3.0 + time * 2.0) * 0.4;
    float c3 = sin(r * 4.0 + time * 3.0) * 0.3;

    // Combine layers and add pulsing effect
    float c = abs(c1 + c2 + c3);
    c *= sin(r * 50.0 + time) * 0.5;

    // Convert to color using HSL
    float hue = sin(time + r * 20.0) * 0.5 + 0.5;
    vec3 col = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add noise for extra texture
    col += vec3(noise(uv * 20.0 + time) * 0.1);

    return vec4(col, 1.0);
}
