vec4 Effect2(vec2 uv) {
    // Create a base pattern using sine functions
    vec2 st = uv * 10.0;
    float time = iTime * 0.5;

    // Add noise and turbulence
    vec3 noisePattern = vec3(
        noise(st + time),
        noise(st + time * 0.7 + 100.0),
        noise(st + time * 0.4 + 200.0)
    );

    // Create geometric patterns with smooth transitions
    float pattern = sin(st.x * 0.5 + time) * cos(st.y * 0.5) +
                   sin(st.y * 0.7 + time * 0.6) * cos(st.x * 0.3);

    // Add turbulence to create dynamic movement
    pattern += turbulent(vec2(st.x * 0.1, st.y * 0.1)) * 0.5;

    // Generate vibrant colors using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        fract(time * 0.2) * 360.0,
        0.7 + noisePattern.x * 0.3,
        0.5 + pattern * 0.4
    ));

    // Create a glowing effect by adding brightness
    float glow = smoothstep(0.0, 1.0, sin(pattern * 2.0 + time));
    color *= glow;

    return vec4(color, 1.0);
}
