vec4 Effect2(vec2 uv) {
    // Create a base pattern using sine functions
    float pattern = sin(uv.x * 10.0 + iTime * 2.0) +
                    sin(uv.y * 10.0 + iTime * 3.0) +
                    sin((uv.x + uv.y) * 5.0 + iTime * 4.0);

    // Add some turbulence for more dynamic movement
    vec2 st = uv * 10.0;
    float turb = turbulent(st + vec2(iTime, 0.0)) * 0.5;

    // Combine pattern and turbulence
    float value = (pattern * 0.3) + turb;

    // Create vibrant colors using HSL to RGB conversion
    vec3 color = hsl(
        sin(value * 10.0 + iTime) * 0.5 + 0.5,  // Hue variation
        0.8,                                      // Saturation
        0.5                                       // Lightness
    );

    return vec4(color, 1.0);
}
