vec4 Effect2(vec2 uv) {
    // Animate rotation based on time
    float angle = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(angle)*uv.x - sin(angle)*uv.y,
        sin(angle)*uv.x + cos(angle)*uv.y
    ) * 0.8 + 0.5;
    // Create a pattern with noise and turbulence
    float noiseValue = turbulent(rotatedUv * 4.0);
    vec3 color = hsl2rgb(vec3(0.6, 0.7, 0.5));

    // Add variation based on noise
    color *= mix(1.0, 1.5, noiseValue);
    color += vec3(turbulent(rotatedUv * 8.0) * 0.2);
    // Brightness modulation over time
    float brightness = sin(iTime * 0.7 + PI) * 0.5 + 0.5;
    color *= mix(1.0, 1.3, brightness);

    return vec4(color, 1.0);
}
