vec4 Effect2(vec2 uv) {
    // Create a time-varying component
    float time = iTime * 0.1;

    // Scale coordinates for different color channels
    vec2 scaleR = uv * 10.0;
    vec2 scaleG = uv * 8.0;
    vec2 scaleB = uv * 6.0;

    // Generate noise values with time offset
    float rNoise = turbulent(scaleR + time);
    float gNoise = turbulent(scaleG + time * 2.0);
    float bNoise = turbulent(scaleB + time * 3.0);

    // Create a base color that shifts over time
    vec3 baseColor = hsl2rgb(vec3(
        uv.x + uv.y + time,
        1.0,
        0.5
    ));

    // Combine noise with color and position
    vec3 finalColor = vec3(
        baseColor.r * (rNoise * 0.6 + 0.4),
        baseColor.g * (gNoise * 0.6 + 0.4),
        baseColor.b * (bNoise * 0.6 + 0.4)
    );

    return vec4(finalColor, 1.0);
}
