vec4 Effect2(vec2 uv) {
    vec3 rgb = vec3(0.0);

    // Create dynamic sine wave patterns
    float timeSine = sin(iTime * 0.5);
    float pattern1 = sin(uv.x * 8.0 + iTime) * 0.5 + 0.5;
    float pattern2 = sin(uv.y * 4.0 + iTime * 0.7) * 0.5 + 0.5;

    // Add turbulence for more complex patterns
    float turb = turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;

    // Combine patterns with color transformations
    rgb.r += pattern1 * (sin(iTime) * 0.5 + 0.5);
    rgb.g += pattern2 * (cos(iTime) * 0.5 + 0.5);
    rgb.b += turb * 0.3;

    // Add noise for texture
    vec3 noiseColors = vec3(
        noise(uv + sin(iTime)),
        noise(uv * 2.0 + iTime),
        noise(uv * 3.0 - iTime)
    );

    // Mix colors and add noise
    rgb = mix(rgb, noiseColors, 0.3);

    // Convert to HSL for better color control
    vec3 hslColor = hsl(
        (uv.x + uv.y) * pi,
        sin(iTime * 0.5) * 0.5 + 0.5,
        turb * 0.7 + 0.3
    );

    // Combine both color approaches
    rgb = mix(rgb, hsl2rgb(hslColor), 0.6);

    return vec4(hsv2rgb(vec3(
        (uv.x + uv.y) * pi,
        sin(iTime * 0.5) * 0.5 + 0.5,
        turb * 0.7 + 0.3
    )), 1.0);
}
