vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 centeredUV = uv * 2.0 - 1.0;
    float theta = atan2(centeredUV.y, centeredUV.x);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create color transitions using HSL
    vec3 hslColor;
    hslColor.x = mod(theta + iTime * 0.5, PI * 2.0) / (PI * 2.0);
    hslColor.y = 1.0; // Full saturation

    // Add pulsing effect to lightness
    float pulse = sin(iTime * 0.5) * 0.3 + 0.7;
    hslColor.z = pulse * (length(centeredUV) * 3.0 + 1.0);

    // Convert HSL to RGB and return
    return vec4(hsl2rgb(hslColor), 1.0);
}
