vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Calculate distance from center
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Central pulsing orb
    float orb = sin(r * 10.0 + iTime) * 0.5 + 0.5;
    orb *= turbulent(vec3(st * 4.0, iTime * 0.1)) * 0.5 + 0.5;

    // Swirling vortex
    theta += iTime * 0.2;
    float swirl = sin(theta * 4.0 + iTime) * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(swirl * 0.6 + 0.3, 1.0, 0.8));

    // Combine effects
    float blend = step(0.4, r);
    vec3 finalColor = mix(color, vec3(orb), blend);

    return vec4(finalColor, 1.0);
}
