vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 centeredUV = uv * 2.0 - 1.0;
    float theta = atan2(centeredUV.y, centeredUV.x);
    float r = length(centeredUV);
    // Add time-based rotation
    theta += iTime * 0.5;
    // Create color gradient based on polar coordinates
    vec3 colorHSL = vec3(
        mod(theta / (pi * 2.0) + noise(uv * 0.5), 1.0),  // Hue variation with noise
        0.7,  // Saturation
        0.5 + turbulent(vec2(r, theta)) * 0.4  // Value with turbulence
    );
    // Convert HSL to RGB and add pulsing effect
    colorHSL.r = mod(colorHSL.r + sin(iTime * 0.3) * 0.5, 1.0);
    vec3 finalColor = hsl2rgb(colorHSL);
    return vec4(finalColor, 1.0);
}
