vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Create grid lines using sine functions
    float xLine = sin(st.x + iTime) * 0.5 + 0.5;
    float yLine = sin(st.y + iTime) * 0.5 + 0.5;

    // Combine lines and add turbulence
    float grid = min(xLine, yLine);
    grid += turbulent(vec2(uv * 10.0 + iTime));

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.5 + st.x) * 0.5 + 0.5,
        0.7,
        0.6
    ));

    return vec4(color * grid, 1.0);
}
