vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float a = atan(st.y, st.x);
    float r = length(st);

    // Add rotation based on time
    a += iTime * 0.5; // Speed of rotation

    // Create geometric pattern using sine waves
    float pattern = sin(a) * 4.0;
    pattern = abs(pattern);

    // Add turbulence for dynamic effect
    vec2 noiseUV = uv * 100.0 + iTime * 0.5;
    float turb = turbulent(noiseUV);

    // Create color transitions using HSL to RGB conversion
    vec3 color;
    color.x = sin(iTime * 0.2 + a) * 0.5 + 0.5; // Hue variation
    color.y = 1.0; // Saturation
    color.z = abs(sin(r * 4.0 + iTime)); // Lightness variation

    // Convert HSL to RGB and mix with turbulence
    vec3 finalColor = hsl2rgb(color) * (1.0 - turb * 0.5);

    return vec4(finalColor, 1.0);
}
