vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Base color transition using time and position
    vec3 color = hsl(
        sin(time + uv.x * 10.0) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Add dynamic noise pattern
    float noiseScale = 4.0;
    vec2 noisePos = uv * noiseScale + time;
    float noiseValue = noise(noisePos);

    // Create pulsing effect using turbulence
    float pulse = turbulent(vec3(uv * 10.0, time)) * 0.5 + 0.5;

    // Combine effects with color transformation
    vec3 finalColor = hsl2rgb(
        vec3(
            (color.r + noiseValue) * 0.5,
            color.g + pulse * 0.3,
            color.b + sin(time) * 0.2
        )
    );

    return vec4(finalColor, 1.0);
}
