vec4 Effect2(vec2 uv) {
    // Transform UV coordinates to range [-1,1]
    vec2 st = uv * 2.0 - 1.0;

    // Create rotating pattern using time
    float angle = iTime * 0.5;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle),
                         sin(angle), cos(angle));

    // Apply rotation and mix with noise
    vec2 rotated = rotMatrix * st;
    float noiseVal = turbulent(rotated * 4.0 + iTime) * 0.5 + 0.5;

    // Calculate distance from center
    float dist = length(st);

    // Create color based on position and time
    vec3 color = hsl2rgb(vec3(
        (dist * 0.6 + noiseVal * 0.4) * 360.0,
        0.7,
        0.5 + sin(iTime) * 0.2
    ));

    return vec4(color, 1.0);
}
