vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based rotation and scaling
    theta += iTime * 0.5;
    st.x *= cos(theta) * (1.0 + sin(iTime * 0.3));
    st.y *= sin(theta) * (1.0 + cos(iTime * 0.3));
    // Create color variations using noise and time
    vec2 noiseCoords = st * 5.0;
    float n = noise(noiseCoords);

    // Generate RGB colors with shifting hues
    vec3 color1 = hsl2rgb(vec3(0.5 + sin(iTime * 0.2) * 0.5, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.7 + cos(iTime * 0.3) * 0.5, 1.0, 0.6));

    // Mix colors based on noise and position
    vec3 finalColor = mix(color1, color2, n);
    finalColor *= sin(r * 8.0 + iTime) * 0.5 + 0.5;
    return vec4(finalColor, 1.0);
}
