vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Rotate over time

    // Create color wheel with HSL colors
    vec3 colorWheel = hsl(theta / (2.0 * PI), 1.0 - 0.5*r, 0.5);

    // Add pulsing center effect
    float pulse = sin(iTime) * 4.0;
    float radial = r + turbulent(vec3(st.x, st.y, iTime)) * 0.2;
    vec3 colorPulse = hsl(0.5, 1.0 - abs(radial - 0.5 - pulse), 0.5);

    // Combine effects
    vec3 finalColor = mix(colorWheel, colorPulse, smoothstep(0.7, 1.0, r));

    return vec4(finalColor, 1.0);
}
