vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 center = vec2(0.5);
    float r = length(uv - center);
    float theta = atan2(uv.y - center.y, uv.x - center.x);

    // Add rotation over time
    theta += iTime * 2.0 + sin(iTime) * 0.5;

    // Create color based on polar coordinates and time
    vec3 color = hsl2rgb(vec3(
        (theta / (pi * 2.0)) + iTime,
        1.0,
        0.5 + turbulent(vec2(r, theta)) * 0.5
    ));

    // Add noise variation
    float noiseFactor = turbulent(vec2(uv * 4.0));
    color = mix(color, hsl2rgb(vec3(
        (r / 2.5) + iTime,
        1.0,
        0.5
    )), noiseFactor);

    // Add brightness variation
    float brightness = sin(iTime) * 0.5 + 0.7;
    color *= brightness;

    return vec4(color, 1.0);
}
