vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create multiple overlapping sine wave layers with different frequencies
    float freq1 = sin(uv.x * 8.0 + iTime * 2.0) * 0.5 + 0.5;
    float freq2 = sin(uv.y * 4.0 + iTime * 3.0 + pi) * 0.5 + 0.5;
    float freq3 = sin((uv.x + uv.y) * 16.0 + iTime * 5.0) * 0.5 + 0.5;

    // Combine frequencies with noise and turbulence
    float pattern = (freq1 + freq2 + freq3) / 3.0;
    pattern += turbulent(uv * 4.0 + vec2(iTime)) * 0.3;
    pattern = mod(pattern, 1.0);

    // Create shifting RGB colors using HSL
    float hue = iTime * 0.1 + noise(vec3(uv * 4.0, iTime)) * 0.5;
    vec3 hslColor = vec3(hue, 1.0, pattern);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix colors from different layers
    color += rgbColor * (pattern * 0.6 + 0.4);
    color += vec3(sin(iTime) * 0.5 + 0.5) * (1.0 - pattern);

    return vec4(color, 1.0);
}
