vec4 Effect2(vec2 uv) {
    // Create time-varying coordinates
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime*0.7)*2.0);

    // Add multiple sine waves with different frequencies
    float wave1 = sin(st.x + iTime) * 0.5;
    float wave2 = sin(st.y + iTime*0.5) * 0.3;
    float wave3 = sin(st.x*2.0 - iTime) * 0.4;

    // Combine waves and add turbulence
    float noiseVal = noise(vec2(st.x, st.y)) * 0.3;
    noiseVal += turbulent(vec2(st.x*0.5 + iTime, st.y*0.7));

    // Create color from HSL values
    vec3 color = hsl2rgb(vec3(
        sin(iTime*0.3)*0.5 + 0.5,
        0.8,
        0.6
    ));

    // Mix noise with color and create final output
    vec3 finalColor = mix(color, vec3(noiseVal), abs(sin(iTime))*0.4);

    return vec4(finalColor, 1.0);
}
