vec4 Effect2(vec2 uv) {
    // Offset UV coordinates based on time for rotation effect
    vec2 offset = vec2(sin(iTime), cos(iTime)) * 0.5;
    vec2 st = uv + offset;

    // Create a base color using HSL with time-based hue
    vec3 color = hsl(st.x * pi + iTime, 1.0, 0.5);

    // Add turbulent noise to create dynamic variations
    float noiseValue = turbulent(vec2(st.x * 100.0, st.y * 100.0));
    color += vec3(noiseValue * 0.5);

    return vec4(color, 1.0);
}
