vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates with time
    vec2 st = uv * 10.0 + sin(iTime * 0.5) * 3.0;

    // Create a base pattern using noise and turbulence
    float noiseValue = noise(st * 0.5) * 0.5 + turbulent(st * 0.25);

    // Generate color components with smooth transitions
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.1 + st.x * 0.2) * 0.5 + 0.5,
        abs(sin(iTime * 0.2 + st.y * 0.3)) * 0.7 + 0.3,
        noiseValue * 0.5 + 0.5
    ));

    // Add dynamic movement using sine functions
    color *= sin(iTime * 0.1 + uv.x * 8.0) * 0.5 + 0.5;
    color += turbulent(vec2(uv.x * 4.0, iTime)) * 0.3;

    return vec4(color, 1.0);
}
