vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 centeredUv = uv - 0.5;

    // Calculate distance from center and scale it
    float dist = length(centeredUv) * 2.0;

    // Add pulsation effect using time
    float t = iTime * 0.5;
    dist += sin(t) * 0.3 + cos(t * 1.5) * 0.2;

    // Apply turbulence to create dynamic variations
    vec2 noiseCoords = vec2(dist, t);
    dist += turbulent(noiseCoords) * 0.1;

    // Create color transitions using HSL
    float hue = t * 2.0 + dist * 5.0;
    hue += sin(t + dist * 3.14) * 0.5;

    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Blend colors for smoother transitions
    float blend = smoothstep(0.0, 1.0, sin(t + dist * 2.0) * 0.5 + 0.5);
    color = mix(color, hsl2rgb(vec3(hue + 1.0, 1.0, 0.5)), blend);

    return vec4(color, 1.0);
}
