vec4 Effect2(vec2 uv) {
    // Combine UV with time-based sine waves
    vec2 st = uv;
    float time = iTime * 0.5;  // Scale time for slower movement

    // Create flowing patterns using sine waves
    vec2 flow = vec2(
        sin(st.x * 10.0 + time) * 0.5,
        sin(st.y * 10.0 + time + pi) * 0.5
    );

    // Add turbulence to create more dynamic movement
    float turb = turbulent(st * 10.0 + flow * 2.0);

    // Combine with noise for texture
    vec3 color = hsl(
        sin(time * 0.2) * 0.5 + 0.5,  // Hue variation over time
        0.7 + turb * 0.3,            // Saturation influenced by turbulence
        0.5                         // Lightness
    );

    // Final color with noise modulation
    float n = noise(st * 10.0 + flow);
    return vec4(color * (n * 0.5 + 0.5), 1.0);
}
