vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal pattern
    float radius = length(uv * 0.5);
    float angle = atan2(uv.y, uv.x);

    // Create pulsating effect using time and radius
    float pulse = sin(iTime + radius * 10.0) * 0.5 + 0.5;

    // Generate hexagonal pattern by repeating every 60 degrees (pi/3 radians)
    angle = mod(angle, pi / 3.0);

    // Create color shifts using HSL
    vec3 colorHSL = vec3(
        mod(angle * 180.0 / pi + iTime * 2.0, 360.0) / 360.0,
        0.7,
        pulse
    );

    // Convert to RGB and add noise for more dynamic effect
    vec3 color = hsl2rgb(colorHSL);
    color += turbulent(uv * 10.0) * 0.5;

    return vec4(color, 1.0);
}
