vec4 Effect2(vec2 uv) {
    // Create a base frequency using UV coordinates and time
    float freq = 4.0 * pi;

    // Create undulating patterns using sine functions
    float pattern1 = sin(uv.x * freq + iTime * 2.0);
    float pattern2 = sin(uv.y * freq - iTime * 2.0);

    // Combine the patterns with noise for more complexity
    float combined = (pattern1 + pattern2) / 2.0;
    combined += turbulent(vec3(uv, iTime)) * 0.5;

    // Create a color gradient using HSL conversion
    vec3 color = hsl(
        fract(combined * 0.5 + iTime * 0.1) * 360.0,
        0.7,
        0.5
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
