vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time variation
    vec2 st = uv * 8.0 + sin(iTime * 0.5) * 3.0;

    // Create RGB values from sine functions with different frequencies
    float r = sin(st.x * 0.5 + iTime) * 0.5 + 0.5;
    float g = sin(st.y * 0.7 - iTime * 0.3) * 0.5 + 0.5;
    float b = sin((st.x + st.y) * 0.4 + iTime) * 0.5 + 0.5;

    // Add turbulence for more dynamic patterns
    vec2 noiseSt = uv * 16.0 + sin(iTime) * 8.0;
    float t = turbulent(noiseSt);

    // Combine color and turbulence
    vec3 color = hsl2rgb(vec3(
        (r + g + b) * 0.333,
        0.7 + t * 0.3,
        0.5 + sin(iTime * 0.2) * 0.5
    ));

    return vec4(color, 1.0);
}
