vec4 Effect2(vec2 uv) {
    // Create base sine wave patterns with varying frequencies and phases
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0);
    float wave2 = sin(uv.y * 4.0 + iTime * 3.0);

    // Combine waves and add noise for complexity
    vec2 noiseCoords = uv * 5.0 + vec2(iTime, iTime) * 2.0;
    float noiseValue = noise(noiseCoords);
    float pattern = wave1 * wave2 + noiseValue * 0.5;

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.5) * 0.5 + 0.5,   // Hue variation over time
        0.7,                             // Saturation
        0.6                              // Lightness
    ));

    // Add interactivity based on mouse movement (simulated with uv)
    vec2 mouse = uv;
    float interact = length(mouse - vec2(0.5)) * 2.0;
    pattern += sin(interact * 10.0 + iTime) * 0.3;

    // Final color calculation
    vec3 finalColor = color * (abs(pattern) * 0.6 + 0.4);

    return vec4(finalColor, 1.0);
}
