vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Create multiple layers of particles
    for(float i = 0.0; i < 5.0; i++) {
        // Scale coordinates for different particle sizes
        vec2 st = uv * pow(1.5, i) + iTime * 0.5;

        // Use noise to generate random positions
        float n = noise(st * 4.0);

        // Create circular particles with glow effect
        vec2 pos = fract(st) - 0.5;
        float dist = length(pos);
        float angle = atan2(pos.y, pos.x) + iTime * 0.1 + i * 0.3;

        // Color based on position and time
        vec3 color = hsv2rgb(vec3(
            fract(iTime * 0.1 + pos.x * 0.5 + pos.y * 0.5),
            0.7,
            0.8
        ));

        // Add rotation effect
        float rotate = sin(angle) * 0.5 + 0.5;
        color *= rotate;

        // Draw particles with glow
        if(dist < 0.4) {
            finalColor += color * (1.0 - dist / 0.4);
        }

        // Add some noise variation
        finalColor += n * 0.2;
    }

    // Mix with background
    vec3 bg = hsv2rgb(vec3(iTime * 0.1, 0.1, 0.2));
    finalColor = mix(bg, finalColor, finalColor.r + finalColor.g + finalColor.b);

    return vec4(finalColor, 1.0);
}
