vec4 Effect2(vec2 uv) {
    // Create base color with time-based hue shift
    vec3 hslColor = vec3(
        sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Add noise and turbulence to create dynamic patterns
    float noiseValue = noise(uv * 20.0);
    float turbulentValue = turbulent(vec3(uv, iTime));

    // Mix in sine-based animation
    vec3 animatedColor = vec3(
        sin(uv.x * 10.0 + iTime) * 0.5 + 0.5,
        sin(uv.y * 10.0 + iTime * 0.5) * 0.5 + 0.5,
        sin(iTime * 0.2) * 0.5 + 0.5
    );

    // Combine everything and convert to RGB
    vec3 finalColor = hsl2rgb(
        hslColor +
        animatedColor * 0.3 +
        noiseValue * 0.2 +
        turbulentValue * 0.1
    );

    return vec4(finalColor, 1.0);
}
