vec4 Effect2(vec2 uv) {
    // Create base color from UV coordinates with time influence
    vec3 hslColor = vec3(uv.x * 0.5 + 0.5, uv.y * 0.5 + 0.5, sin(iTime * 0.1 + uv.x * 2.0));

    // Add noise texture sampling with time offset
    vec2 noiseUV = uv * 40.0 + fract(iTime * 0.5);
    vec3 noiseColor = texture(iChannel0, noiseUV).rgb;

    // Mix HSL and noise colors with smooth transitions
    vec3 finalColor = mix(hsl2rgb(hslColor), noiseColor, 0.7);

    // Add flowing movement effect
    float flowX = sin(uv.y * 10.0 + iTime) * 0.5;
    float flowY = cos(uv.x * 10.0 + iTime * 0.8) * 0.5;
    vec2 flowUV = uv + vec2(flowX, flowY);

    // Apply turbulence to create melting effect
    float turb = turbulent(flowUV * 40.0 + iTime);
    finalColor += turb * 0.3;

    return vec4(finalColor, 1.0);
}
