vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create time-based animation
    float time = iTime * 0.5;

    // Add rotation effect
    float angle = sin(time) * 0.5 + pi;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;

    // Create color shifting pattern using sine functions
    vec3 color = vec3(
        sin(rotated.x * 4.0 + time) * 0.5 + 0.5,
        sin(rotated.y * 4.0 + time + 2.0) * 0.5 + 0.5,
        sin((rotated.x + rotated.y) * 3.0 + time + 4.0) * 0.5 + 0.5
    );

    // Add turbulence for more dynamic effect
    vec2 noiseUV = st * 0.5 + vec2(time, 0.0);
    float turb = turbulent(noiseUV);
    color += turb * 0.3;

    return vec4(color, 1.0);
}
