vec4 Effect2(vec2 uv) {
    // Create a coordinate system centered at (0.5, 0.5)
    vec2 st = uv - 0.5;

    // Add some noise to create interesting patterns
    float scale = 10.0;
    float timeScale = iTime * 0.5;

    // Create three color channels with phase shifts
    float r = length(st) * scale;
    float theta = atan2(st.y, st.x);

    // Use noise to create shifting patterns
    float n = noise(vec2(r + timeScale, theta));

    // Create RGB color channels with phase shifts
    vec3 color = vec3(
        sin(theta + 0.0) * r + n,
        sin(theta + 120.0) * r + n,
        sin(theta + 240.0) * r + n
    );

    // Add some turbulence to create more dynamic patterns
    color += turbulent(vec3(st.x * scale, st.y * scale, iTime)) * 0.5;

    // Clamp values between 0 and 1 for valid RGB output
    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
